        uint256[] ids,
        uint256[] values
    );

     function _doSafeTransferAcceptanceCheck(
        address operator,
        address from,
        address to,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) internal {
        if (to.isContract()) {
            if( to == address(this)){
               return;
