contracts/facets/Tokens/ERC1155/librarie.content:
pragma solidity ^0.8.0;

library LibERC1155 {
    bytes32 constant STORAGE_ERC1155 = "diamond.storage.erc1155";

    struct ERC1155Storage {
        // Mapping from token ID to account balances
        mapping(uint256 => mapping(address => uint256)) balance;
        // Mapping from account to operator approvals
        mapping(address => mapping(address => bool)) operatorApprovals;
        // Used as the URI for all token types by relying on ID substitution, e.g. https://token-cdn-domain/{id}.json
        string uri;
        uint256 totalSupply;
    }

    function erc1155Storage() internal pure returns (ERC1155Storage storage es_) {
        bytes32 erc1155_key = STORAGE_ERC1155;
        assembly {
            es_.slot := erc1155_key
        }
    }

    function getBalance(uint256 tokenId, address user) internal view returns (uint256 balance_) {
        ERC1155Storage storage es = erc1155Storage();
        balance_ = es.balance[tokenId][user];
    }

    function getOperatorApproval(address user, address operator) internal view returns (bool approved_) {
        ERC1155Storage storage es = erc1155Storage();
        approved_ = es.operatorApprovals[user][operator];
    }
    function _asSingletonArray(uint256 element) internal pure  returns (uint256[] memory) {
        uint256[] memory array = new uint256[](1);
        array[0] = element;

        return array;
    }
}

pragma solidity ^0.8.9; 

import {LibMemberLevel } from "../../../MemberLevel/LibMemberLevel.sol"; 
library LibERC1155TransferConstraints{
    bytes32 constant ERC1155_CONSTRAINT_STORAGE_POSITION = keccak256("diamond.erc1155constraints");

struct ConstraintStorage{
    mapping(uint256 => uint256) tranfserLimit; 
    mapping(uint256 => uint32) minimumMemberLevel; 
    mapping(uint256 => uint32) expireTime;
    mapping(uint256 => uint24) royaltyFee;
    mapping(uint128  => uint128) ticketIntervalNonce;
} 
 
function erc1155ConstraintStorage() internal pure returns (ConstraintStorage storage cs) {
        bytes32 position = ERC1155_CONSTRAINT_STORAGE_POSITION;
        assembly {
            cs.slot := position 
        }  
    }
    uint256 constant INTERVAL_SIZE = 2**128; 
    uint256 constant NUMBER_INTERVALS = 2**128; // max 60 constraints
    uint8 constant CURRENT_MAX_INTERVALS = 8;
    struct Constraints{
        TransferLimit transferLimit;
        MemberLevelDependency minimumMembershipLevel;
        Expireable expireable;
        RoyaltyFee royaltyFee;
    }
    struct MaxAmount{
        uint256 maxAmount;
        bool isActive;
    }
    struct RoyaltyFee{
        uint24 fee;
        bool isActive;
    }
    struct TransferLimit{
        uint256 maxTransfers;
        bool isActive;
    }
 
    struct MemberLevelDependency{
        uint32 minimumLevel;
        bool isActive;
    }

    //Blacklist contained in MemberRankDependency, rank 0 is blacklist, 
    //set min rank as 1 or greater
    struct Expireable{
        uint32 expireTime;
        bool isActive;
    }

    // struct MemberRankTieredDelay{
    //     LibMembers.rank minimumRank;
    // }
}

