contracts/test/relay/MockNFTPositionMana.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

// Mock Uniswap interface
interface IMockUniswap {
    function swap(address tokenIn, address tokenOut, uint256 amountIn) external returns (uint256 amountOut);
}

/**
 * @title MockNFTPositionManager
 * @notice A simplified version of Uniswap V3's NonfungiblePositionManager for testing purposes
 */
contract MockNFTPositionManager is ERC721, Ownable {
    using Counters for Counters.Counter;

    // Token ID tracker
    Counters.Counter private _tokenIdCounter;

    // Reference to the mock Uniswap contract
    IMockUniswap public mockUniswap;

    // Liquidity position information
    struct Position {
        address token0;
        address token1;
        uint24 fee;
        int24 tickLower;
        int24 tickUpper;
        uint128 liquidity;
        uint256 amount0;
        uint256 amount1;
        uint256 feeGrowthInside0LastX128;
        uint256 feeGrowthInside1LastX128;
        uint128 tokensOwed0;
        uint128 tokensOwed1;
    }

    // Mapping from token ID to position info
    mapping(uint256 => Position) public positionInfo;

    // Events
    event PositionCreated(uint256 indexed tokenId, address indexed owner, address token0, address token1, uint128 liquidity);
    event PositionIncreased(uint256 indexed tokenId, uint128 liquidityAdded);
    event PositionDecreased(uint256 indexed tokenId, uint128 liquidityRemoved);
    event FeesCollected(uint256 indexed tokenId, uint128 amount0, uint128 amount1);

    constructor(address _mockUniswapAddress) ERC721("Mock Uniswap V3 Positions", "UNI-V3-POS") Ownable() {
        mockUniswap = IMockUniswap(_mockUniswapAddress);
    }

    /**
     * @notice Mint a new position
     * @param recipient The address to mint the position to
     * @param token0 The first token of the pool
     * @param token1 The second token of the pool
     * @param fee The fee tier of the pool
     * @param tickLower The lower tick bound of the position
     * @param tickUpper The upper tick bound of the position
     * @param amount0Desired The amount of token0 to add as liquidity
     * @param amount1Desired The amount of token1 to add as liquidity
     * @return tokenId The ID of the minted token
     * @return liquidity The amount of liquidity added
     * @return amount0 The amount of token0 actually added
     * @return amount1 The amount of token1 actually added
     */
    function mint(
        address recipient,
        address token0,
        address token1,
        uint24 fee,
        int24 tickLower,
        int24 tickUpper,
        uint256 amount0Desired,
        uint256 amount1Desired
    ) external returns (
        uint256 tokenId,
        uint128 liquidity,
        uint256 amount0,
        uint256 amount1
    ) {
        // For simplicity, we'll assume all liquidity gets added
        amount0 = amount0Desired;
        amount1 = amount1Desired;
        liquidity = uint128(amount0 + amount1); // Simplified liquidity calculation

        // Mint the position token
        _tokenIdCounter.increment();
        tokenId = _tokenIdCounter.current();
        _mint(recipient, tokenId);

        // Store position data
        positionInfo[tokenId] = Position({
            token0: token0,
            token1: token1,
            fee: fee,
            tickLower: tickLower,
            tickUpper: tickUpper,
            liquidity: liquidity,
            amount0: amount0,
            amount1: amount1,
            feeGrowthInside0LastX128: 0,
            feeGrowthInside1LastX128: 0,
            tokensOwed0: 0,
            tokensOwed1: 0
        });

        emit PositionCreated(tokenId, recipient, token0, token1, liquidity);
        return (tokenId, liquidity, amount0, amount1);
    }

    /**
     * @notice Increases the amount of liquidity in a position
     * @param tokenId The ID of the token for which liquidity is being increased
     * @param amount0Desired The desired amount of token0 to be added
     * @param amount1Desired The desired amount of token1 to be added
     * @return liquidity The new liquidity amount
     * @return amount0 The amount of token0 actually added
     * @return amount1 The amount of token1 actually added
     */
    function increaseLiquidity(
        uint256 tokenId,
        uint256 amount0Desired,
        uint256 amount1Desired
    ) external returns (
        uint128 liquidity,
        uint256 amount0,
        uint256 amount1
    ) {
        require(_exists(tokenId), "Position does not exist");
        require(ownerOf(tokenId) == msg.sender, "Not position owner");

        Position storage position = positionInfo[tokenId];
        
        // For simplicity, we'll assume all liquidity gets added
        amount0 = amount0Desired;
        amount1 = amount1Desired;
        liquidity = uint128(amount0 + amount1); // Simplified liquidity calculation

        // Update position data
        position.liquidity += liquidity;
        position.amount0 += amount0;
        position.amount1 += amount1;

        emit PositionIncreased(tokenId, liquidity);
        return (position.liquidity, amount0, amount1);
    }

    /**
     * @notice Decreases the amount of liquidity in a position and accounts for fees earned
     * @param tokenId The ID of the token for which liquidity is being decreased
     * @param liquidity The amount by which liquidity will be decreased
     * @return amount0 The amount of token0 sent to recipient
     * @return amount1 The amount of token1 sent to recipient
     */
    function decreaseLiquidity(
        uint256 tokenId,
        uint128 liquidity
    ) external returns (
        uint256 amount0,
        uint256 amount1
    ) {
        require(_exists(tokenId), "Position does not exist");
        require(ownerOf(tokenId) == msg.sender, "Not position owner");
        
        Position storage position = positionInfo[tokenId];
        require(position.liquidity >= liquidity, "Insufficient liquidity");

        // Calculate amounts to withdraw based on liquidity proportion
        uint256 proportion = (uint256(liquidity) * 1e18) / uint256(position.liquidity);
        amount0 = (position.amount0 * proportion) / 1e18;
        amount1 = (position.amount1 * proportion) / 1e18;

        // Update position data
        position.liquidity -= liquidity;
        position.amount0 -= amount0;
        position.amount1 -= amount1;

        // Add fees to accumulated fees (simplified fee calculation)
        position.tokensOwed0 += uint128(amount0 / 100); // 1% fee
        position.tokensOwed1 += uint128(amount1 / 100); // 1% fee

        emit PositionDecreased(tokenId, liquidity);
        return (amount0, amount1);
    }

    /**
     * @notice Collects tokens owed to a position
     * @param tokenId The ID of the NFT for which tokens are being collected
     * @return amount0 The amount of token0 collected
     * @return amount1 The amount of token1 collected
     */
    function collect(
        uint256 tokenId
    ) external returns (
        uint128 amount0,
        uint128 amount1
    ) {
        require(_exists(tokenId), "Position does not exist");
        require(ownerOf(tokenId) == msg.sender, "Not position owner");
        
        Position storage position = positionInfo[tokenId];
        
        // Get accumulated fees
        amount0 = position.tokensOwed0;
        amount1 = position.tokensOwed1;
        
        // Reset accumulated fees
        position.tokensOwed0 = 0;
        position.tokensOwed1 = 0;

        emit FeesCollected(tokenId, amount0, amount1);
        return (amount0, amount1);
    }

    /**
     * @notice Burns a token
     * @param tokenId The ID of the token to burn
     */
    function burn(uint256 tokenId) external {
        require(_exists(tokenId), "Position does not exist");
        require(ownerOf(tokenId) == msg.sender, "Not position owner");
        
        Position storage position = positionInfo[tokenId];
        require(position.liquidity == 0, "Cannot burn position with liquidity");
        
        _burn(tokenId);
        delete positionInfo[tokenId];
    }

    /**
     * @notice Returns the position information
     * @param tokenId The ID of the token to get info for
     */
    function getPositions(uint256 tokenId) external view returns (
        address token0,
        address token1,
        uint24 fee,
        int24 tickLower,
        int24 tickUpper,
        uint128 liquidity,
        uint256 feeGrowthInside0LastX128,
        uint256 feeGrowthInside1LastX128,
        uint128 tokensOwed0,
        uint128 tokensOwed1
    ) {
        require(_exists(tokenId), "Position does not exist");
        
        Position storage position = positionInfo[tokenId];
        return (
            position.token0,
            position.token1,
            position.fee,
            position.tickLower,
            position.tickUpper,
            position.liquidity,
            position.feeGrowthInside0LastX128,
            position.feeGrowthInside1LastX128,
            position.tokensOwed0,
            position.tokensOwed1
        );
    }

    /**
     * @notice Update the mock Uniswap address
     * @param _mockUniswapAddress The new mock Uniswap address
     */
    function setMockUniswap(address _mockUniswapAddress) external onlyOwner {
        mockUniswap = IMockUniswap(_mockUniswapAddress);
    }
}

