// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title ISwapRouter
 * @notice Interface for the Uniswap V3 SwapRouter
 */
interface ISwapRouter {
    struct ExactOutputSingleParams {
        address tokenIn;
        address tokenOut;
        uint24 fee;
        address recipient;
        uint256 deadline;
        uint256 amountOut;
        uint256 amountInMaximum;
        uint160 sqrtPriceLimitX96;
    }

    function exactOutputSingle(ExactOutputSingleParams calldata params) external payable returns (uint256 amountIn);
}

/**
 * @title MockUniswapV3Pool
 * @notice Interface for the mock Uniswap V3 Pool used for testing
 */
interface IMockUniswapV3Pool {
    function exactOutputSingle(
        address tokenIn,
        address tokenOut,
        uint256 amountOut,
        uint256 amountInMaximum
    ) external returns (uint256 amountIn);
}

/**
 * @title MockWETH
 * @notice Interface for WETH with deposit and withdraw functions
 */
interface IWETH {
    function deposit() external payable;
    function withdraw(uint256 amount) external;
    function transfer(address to, uint256 value) external returns (bool);
}

/**
 * @title MockSwapRouter
 * @notice A mock implementation of Uniswap V3's SwapRouter for testing purposes
 * @dev This contract simulates the behavior of the swap router using a mock pool
 */
contract MockSwapRouters is ISwapRouter {
    using SafeERC20 for IERC20;
    
    IMockUniswapV3Pool public mockPool;
    IWETH public weth;
    
    /**
     * @notice Constructor for MockSwapRouter
     * @param _mockPool Address of the mock Uniswap V3 pool
     * @param _weth Address of the WETH contract
     */
    constructor(address _mockPool, address _weth) {
        mockPool = IMockUniswapV3Pool(_mockPool);
        weth = IWETH(_weth);
    }
    
    /**
     * @notice Simulates an exact output swap
     * @param params The parameters for the swap
     * @return amountIn The amount of tokens sent to the pool
     */
    function exactOutputSingle(ExactOutputSingleParams calldata params) external payable override returns (uint256 amountIn) {
        require(block.timestamp <= params.deadline, "Transaction expired");
        
        // Get tokens from caller
        IERC20(params.tokenIn).safeTransferFrom(msg.sender, address(this), params.amountInMaximum);
        
        // Approve tokens to the pool
        IERC20(params.tokenIn).approve(address(mockPool), params.amountInMaximum);
        
        // Execute the swap through the mock pool
        amountIn = mockPool.exactOutputSingle(
            params.tokenIn,
            params.tokenOut,
            params.amountOut,
            params.amountInMaximum
        );
        
        // Handle refund of unused tokens
        if (amountIn < params.amountInMaximum) {
            IERC20(params.tokenIn).safeTransfer(msg.sender, params.amountInMaximum - amountIn);
        }
        
        // If tokenOut is WETH and recipient is specified, unwrap WETH and send ETH
        if (params.tokenOut == address(weth) && params.recipient != address(0)) {
            IERC20(params.tokenOut).safeTransferFrom(address(mockPool), address(this), params.amountOut);
            weth.withdraw(params.amountOut);
            (bool success, ) = params.recipient.call{value: params.amountOut}("");
            require(success, "ETH transfer failed");
        } else {
            // Otherwise just transfer the tokens
            IERC20(params.tokenOut).safeTransferFrom(address(mockPool), params.recipient, params.amountOut);
        }
        
        return amountIn;
    }
    
    /**
     * @notice Allows the contract to receive ETH
     */
    receive() external payable {}
}

