// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./MockWETH.sol";

contract MockUniswapV3Pool {
    address public tokenA;
    address public tokenB;
    
    event Swap(
        address sender,
        address recipient,
        int256 amount0,
        int256 amount1,
        uint160 sqrtPriceX96,
        uint128 liquidity,
        int24 tick
    );
    
    constructor(address _tokenA, address _tokenB) {
        tokenA = _tokenA;
        tokenB = _tokenB;
    }
    
    // Mock exactInputSingle function - simplified version of UniswapV3 swap
    function exactOutputSingle(
        address tokenIn,
        address tokenOut,
        uint24 poolFee,
        address recipient,
        uint256 amountIn,
        uint256 amountOutMinimum,
        uint160 sqrtPriceLimitX96
    ) external returns (uint256 amountOut) {
        require(
            (tokenIn == tokenA && tokenOut == tokenB) || 
            (tokenIn == tokenB && tokenOut == tokenA),
            "Invalid token pair"
        );
        
        // Transfer tokenIn from sender to this contract
        IERC20(tokenIn).transferFrom(msg.sender, address(this), amountIn);
        
        // Calculate output amount (simplified calculation - in real Uniswap it would use the price curve)
        // For testing, we'll just apply a 1% fee and do a simple conversion
        uint256 feeAmount = (amountIn * poolFee) / 1000000;
        amountOut = amountIn - feeAmount;
        
        // Check slippage
        require(amountOut >= amountOutMinimum, "Too little received");
        
        // Transfer tokenOut to recipient
        IERC20(tokenOut).transfer(recipient, amountOut);
        
        // Emit swap event (with mock values for some parameters)
        emit Swap(
            msg.sender,
            recipient,
            int256(amountIn),
            -int256(amountOut),
            sqrtPriceLimitX96,
            1000000,  // Mock liquidity
            0         // Mock tick
        );
        
        return amountOut;
    }
}

