// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import { ReentrancyGuardContract } from "../../ReentrancyGuard.sol";



/**
 * @dev Mock Uniswap Router for testing
 */
contract MockUniswapRouter {
    struct ExactOutputSingleParams {
        address tokenIn;
        address tokenOut;
        uint24 fee;
        address recipient;
        uint256 deadline;
        uint256 amountOut;
        uint256 amountInMaximum;
        uint160 sqrtPriceLimitX96;
    }
    
    event SwapCalled(address tokenIn, address tokenOut, uint256 amountOut);
    
    function exactOutputSingle(ExactOutputSingleParams calldata params) external returns (uint256 amountIn) {
        emit SwapCalled(params.tokenIn, params.tokenOut, params.amountOut);
        return params.amountInMaximum / 2; // Mock return value
    }
}

/**
 * @dev Interface for TrustedForwarder
 */
interface ITrustedForwarder {
    struct ForwardRequest {
        address signer;
        address target;
        bytes targetData;
        uint256 nonce;
        uint32 deadline;
    }
    
    function execute(
        MockUniswapRouter.ExactOutputSingleParams calldata params,
        ForwardRequest calldata req,
        address recipient,
        bytes calldata signature
    ) external returns (bool, bytes memory);
}

/**
 * @dev RelayEntryPoint that calls into TrustedForwarder
 */
contract RelayEntryPoint is ReentrancyGuardContract {
    address public immutable trustedForwarder;
    
    event RelayExecuted(
        address indexed signer,
        address indexed target,
        bool success
    );
    
    constructor(address _trustedForwarder) {
        trustedForwarder = _trustedForwarder;
    }
    
    function relay(
        MockUniswapRouter.ExactOutputSingleParams memory paymasterParams,
        ITrustedForwarder.ForwardRequest calldata req,
        address recipient,
        bytes calldata signature
    ) external ReentrancyGuard {
        // Start gas measurement
        uint256 startGas = gasleft();
        
        // Call the trusted forwarder to execute the transaction
        (bool success, bytes memory result) = trustedForwarder.call(
            abi.encodeWithSelector(
                ITrustedForwarder.execute.selector,
                paymasterParams,
                req,
                recipient,
                signature
            )
        );
        
        // Revert if the call failed, forwarding the revert reason
        if (!success) {
            assembly {
                revert(add(result, 32), mload(result))
            }
        }
        
        emit RelayExecuted(req.signer, req.target, success);
    }
}

