pragma solidity ^0.8.9;

import {MocksERC20} from "./TestERC20.sol";
// Mock Uniswap Router that simulates token swaps
contract MockUniswapRouters {
  address payable public immutable WETH;
  uint256 private outputAmount;
  
  constructor(address payable _weth) {
    WETH = _weth;
  }
  
  // Set the amount of tokens the router will return when swapExactTokensForTokens is called
  function setExpectedOutput(uint256 _amount) external {
    outputAmount = _amount;
  }
  
  function swapExactTokensForTokens(
    uint256 amountIn,
    uint256 amountOutMin,
    address[] calldata path,
    address to,
    uint256 deadline
  ) external returns (uint[] memory amounts) {
    require(path.length >= 2, "Invalid path");
    require(deadline >= block.timestamp, "Expired deadline");
    
    // Transfer output token (WETH) to recipient
    MocksERC20(WETH).transfer(to, outputAmount); 
    
    uint[] memory result = new uint[](2);
    result[0] = amountIn;
    result[1] = outputAmount;
    return result;
  }
}

